function hide_item_price_columns(frm) {
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'User',
            name: frappe.session.user
        },
        callback: function(response) {
            const roles = response.message.roles.map(role => role.role);
            console.log("User Roles:", roles);

            if (roles.includes("Hide Item Price")) {
                // Hide child table fields
                const fields_to_hide = [
                    "rate", "amount", "price_list_rate", "base_price_list_rate", "custom_mrp",
                    "base_rate", "base_amount", "net_rate", "net_amount", "base_net_rate",
                    "base_net_amount", "fully_discount_rate","taxable_value","gst_details_section"
                ];
                fields_to_hide.forEach(field => {
                    frm.get_field("items").grid.toggle_display(field, false);
                });

                // Hide document-level fields
                const doc_level_fields = [
                    "total", "grand_total", "rounding_adjustment", "rounded_total", "in_words","advance_paid","base_total_taxes_and_charges","total_taxes_and_charges"
                ];
                doc_level_fields.forEach(field => {
                    frm.toggle_display(field, false);
                });


                setTimeout(() => {


                    fields_to_hide.forEach(field => {
                        $(`div[data-fieldname='items'] .grid-heading:contains('${field}')`).hide().css("width", "0");
                        $(`div[data-fieldname='items'] .grid-row`).each(function () {
                            $(this).find(`div[data-fieldname='${field}']`).hide().css("width", "0");
                        });
                    });

                    doc_level_fields.forEach(field => {
                        $(`div[data-fieldname='${field}']`).hide().css("width", "0");
                    });
                }, 500);
            }
        }
    });
}

function hide_taxes_price_columns(frm) {
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'User',
            name: frappe.session.user
        },
        callback: function(response) {
            const roles = response.message.roles.map(role => role.role);
            console.log("User Roles:", roles);

            if (roles.includes("Hide Item Price")) {
                const taxes_child_table = "taxes";

                const taxes_fields_to_hide = [
                    "rate","account_currency","tax_amount","base_tax_amount","tax_amount_after_discount_amount",
                    "base_tax_amount_after_discount_amount","total","base_total"
                    ]

                // Hide child table columns using Frappe grid methods
                const tax_grid = frm.get_field(taxes_child_table)?.grid;
                if(tax_grid){
                    taxes_fields_to_hide.forEach(field =>{
                        tax_grid.update_docfield_property(field,"hidden", true);
                    });
                }
                // Also hide using jQuery in case of DOM rendering
                setTimeout(() => {

                    taxes_fields_to_hide.forEach(field =>{
                        $(`div[data-fieldname='${taxes_child_table}'] .grid-heading:contains('${frappe.meta.get_docfield("Sales Taxes and Charges", field)?.label || field}')`).hide();
                        $(`div[data-fieldname='${taxes_child_table}'] .grid-row`).each(function () {
                            $(this).find(`div[data-fieldname='${field}']`).hide();
                        });

                    });

                }, 300);
            }
        }
    });
}
