function hide_rate_and_amount_fields(frm) {
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'User',
            name: frappe.session.user
        },
        callback: function(response) {
            const roles = response.message.roles.map(role => role.role);
            console.log("User Roles:", roles);

            if (roles.includes("Hide Item Price")) {
                // Define the fields you want to hide
                const fields_to_hide = ['rate', 'amount', 'base_rate', 'base_amount','taxable_value']; // Add your fields here
                const doc_level_fields = ['total', 'grand_total', 'rounded_total','total_taxes_and_charges']; // Add your doc level fields here

                setTimeout(() => {
                    console.log("Hiding item grid and doc fields visually...");

                // Add custom CSS to handle grid layout
                if (!$('#custom_grid_style').length) {
                    $('<style id="custom_grid_style"></style>').appendTo('head');
                }

                let style_content = '';
                fields_to_hide.forEach(field => {
                    style_content += `
                        div[data-fieldname='items'] .grid-heading-row .grid-static-col[data-fieldname='${field}'],
                        div[data-fieldname='items'] .grid-row .grid-static-col[data-fieldname='${field}'],
                        div[data-fieldname='items'] .grid-row .grid-field[data-fieldname='${field}'] {
                            display: none !important;
                            padding: 0 !important;
                            width: 0 !important;
                            max-width: 0 !important;
                            overflow: hidden !important;
                        }
                    `;
                });

        // Hide fields in child table (items)
                const item_fields_to_hide = [
                    "rate", 
                    "amount", 
                    "base_rate", 
                    "base_net_rate",
                    "base_amount", 
                    "base_net_amount",
                    "last_purchase_rate",
                    "price_list_rate", 
                    "base_price_list_rate",
                    "discount_amount", 
                    "net_rate", 
                    "net_amount",
                    "stock_uom_rate",
                    "taxable_value"
                ];

                item_fields_to_hide.forEach(field => {
                    frm.get_field("items").grid.toggle_display(field, false);
                });



                $('#custom_grid_style').html(style_content);

                // Fix column layout by adjusting remaining columns
                setTimeout(() => {
                    // This forces the grid to recalculate column widths
                    $(window).trigger('resize');
                    $(`div[data-fieldname='items'] .filter-row`).hide();


                    // If needed, manually distribute space among visible columns
                    const visibleCols = $(`div[data-fieldname='items'] .grid-heading-row .grid-static-col:visible`).length;
                    if (visibleCols > 0) {
                        const idealWidth = (100 / visibleCols) + '%';
                        $(`div[data-fieldname='items'] .grid-heading-row .grid-static-col:visible`).css('width', idealWidth);
                        $(`div[data-fieldname='items'] .grid-row .grid-static-col:visible`).css('width', idealWidth);
                    }
                }, 100);

                // Hide doc level fields
                doc_level_fields.forEach(field => {
                    $(`div[data-fieldname='${field}']`).closest('.frappe-control').hide();
                });
            }, 500);
        }
        }

});
}
