        },
        callback: function(response) {
            const roles = response.message.roles.map(role => role.role);
            console.log("User Roles:", roles);

            if (roles.includes("Hide Item Price")) {
                const grid = frm.get_field("items")?.grid;

                // Hide child table popup fields
                if (grid) {
                    grid.toggle_display("rate", false);
                    grid.toggle_display("amount", false);
                }

                setTimeout(() => {
                    console.log("Hiding Rate and Amount columns in Items grid (Material Request)...");

                    // Hide grid headers
                    $("div[data-fieldname='items'] .grid-heading:contains('Rate')").hide().css("width", "0");
                    $("div[data-fieldname='items'] .grid-heading:contains('Amount')").hide().css("width", "0");

                    // Hide each row's fields
                    $("div[data-fieldname='items'] .grid-row").each(function () {
                        $(this).find("div[data-fieldname='rate']").hide().css("width", "0");
                        $(this).find("div[data-fieldname='amount']").hide().css("width", "0");
                    });

                    // Optional: hide total fields if they exist
                    $("div[data-fieldname='total']").hide().css("width", "0");
                    $("div[data-fieldname='grand_total']").hide().css("width", "0");

                }, 500);
            }
        }
    });
}

