function hide_item_price_columns(frm) {
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'User',
            name: frappe.session.user
        },
        callback: function (response) {
            const roles = response.message.roles.map(role => role.role);
            if (roles.includes("Hide Item Price")) {
                const fields_to_hide = ["rate", "amount"];

                // Hide columns from grid meta
                frm.fields_dict["required_items"].grid.update_docfield_property("rate", "hidden", true);
                frm.fields_dict["required_items"].grid.update_docfield_property("amount", "hidden", true);

                // Force refresh the grid to reflect hidden columns
                frm.refresh_field("required_items");

                // As a fallback, hide column headers and cells via jQuery
                setTimeout(() => {
                    fields_to_hide.forEach(field => {
                        // Hide column headers
                        $(`div[data-fieldname="required_items"] .grid-heading:contains("${frappe.meta.get_docfield("Work Order Item", field).label}")`).hide();

                        // Hide each cell of the corresponding field
                        $(`div[data-fieldname="required_items"] .grid-row`).each(function () {
                            $(this).find(`div[data-fieldname="${field}"]`).hide();
                        });
                    });
                }, 500);
            }
        }
    });
}
